app={};app.initializeCanvasItems=function(){var b=document.getElementsByClassName("canvas-item");app.addEventListenerToClassnames(b);for(var d=0,a=b.length;d<a;d++){var f=b[d];var c=f.id;var e=app.getFromLocalStorage(c);f.innerText=e;}};app.addEventListenerToClassnames=function(b){for(var c=0,a=b.length;c<a;c++){b[c].addEventListener("blur",function(){var d=this.id;var e=this.innerText;app.setToLocalStorage(d,e);});}};app.setToLocalStorage=function(a,b){if(localStorage){localStorage.setItem(a,b);}};app.getFromLocalStorage=function(a){if(localStorage){return localStorage.getItem(a);}};app.clearLocalStorage=function(){if(localStorage){localStorage.clear();}};window.onload=function(){app.initializeCanvasItems();var b=document.getElementById("btn_save");var a=document.getElementById("toast");b.addEventListener("click",function(){console.log("clicked");var c=confirm("Are you sure you clear canvas items?");if(c){app.clearLocalStorage();app.initializeCanvasItems();}});};